/**
* @param {string | number | Date} date
* @param {number} months
*/
function subtractMonths(date, months) {
    // 👇 Make copy with "Date" constructor
    const dateCopy = new Date(date);
    dateCopy.setMonth(dateCopy.getMonth() - months);
    return dateCopy;
}

